const projectsContainer = document.querySelector(".projects-container");
const projectTemplate = document.querySelector("#project-template");
const contactTemplate = document.querySelector("#contact-template");

projects.forEach((project) => {
  const newProject = projectTemplate.content.cloneNode(true);
  const tagTemplate = newProject.querySelector("#tag-template");
  const linkTemplate = newProject.querySelector("#button-template");
  const toolTemplate = newProject.querySelector("#tool-template");

  newProject.querySelector(".description").innerText = project.description;
  newProject.querySelector(".title").innerText = project.title;
  newProject.querySelector(".date").innerText = project.date;
  newProject.querySelector("img").src = project.image;

  project.tags.forEach((tag) => {
    const newTag = tagTemplate.content.cloneNode(true);
    newTag.querySelector(".tag").innerText = tag;
    newProject.querySelector(".tags-container").append(newTag);

    newProject.querySelector(".project").classList.add(getClassName(tag));
  });

  project.links.forEach((link) => {
    const newLink = linkTemplate.content.cloneNode(true);
    newLink.querySelector(".btn-text").innerText = link.title;
    newLink.querySelector("a").href = link.link;
    newProject.querySelector(".buttons-container").append(newLink);
  });

  project.tools.forEach((tool) => {
    const newTool = toolTemplate.content.cloneNode(true);
    newTool.querySelector(".tool-text").innerText = tool.title;
    newTool.querySelector(".tool-icon").innerHTML = tool.icon;
    newProject.querySelector(".tools-container").append(newTool);
  });
  projectsContainer.append(newProject);
});

contacts.forEach((contact) => {
  const newContact = contactTemplate.content.cloneNode(true);
  newContact.querySelector("a").href = contact.link;
  newContact.querySelector(".icon").innerHTML = contact.icon;
  newContact.querySelector(".contact-text").innerText = contact.title;
  document.querySelector(".contacts-container").append(newContact);
});

function reveal() {
  var reveals = document.querySelectorAll(".reveal");

  for (var i = 0; i < reveals.length; i++) {
    var windowHeight = window.innerHeight;
    var elementTop = reveals[i].getBoundingClientRect().top;
    var elementVisible = 150;

    if (elementTop < windowHeight - elementVisible) {
      reveals[i].classList.add("active");
    } else {
      reveals[i].classList.remove("active");
    }
  }
}

window.addEventListener("scroll", reveal);

reveal();

